SELECT DISTINCT p.nr, p.label, p."propertyTex1" FROM product p, producttypeproduct ptp WHERE p.nr=ptp.product AND ptp."productType"=2 AND p.nr IN (SELECT DISTINCT product FROM productfeatureproduct WHERE "productFeature"=12) AND (("propertyNum1">348 AND p.nr IN (SELECT DISTINCT product FROM productfeatureproduct WHERE "productFeature"=20)) OR ("propertyNum2">759 AND p.nr IN (SELECT DISTINCT product FROM productfeatureproduct WHERE "productFeature"=8))) ORDER BY label LIMIT 10 OFFSET 10;
